from pyzbar.pyzbar import decode

class BarcodeScanner:
    def __init__(self, ui):
        self.cap = cv2.VideoCapture(0)
        self.cap.set(3, 640)
        self.cap.set(4, 480)
        self.camera_on = True
        self.scan_barcode()
        self.cursor = None
        self.ui = ui

    def scan_barcode(self):
        success, frame = self.cap.read()
        if success and self.camera_on:
            for code in decode(frame):
                self.cursor.execute(f"SELECT * FROM [cart].[dbo].[Product] WHERE Barcode='{code.data.decode('utf-8')}'")
                rows = self.cursor.fetchall()
                for row in rows:
                    barcode = row[0]
                    image = row[1]
                    name = row[2]
                    weight = row[3]
                    price = row[4]
                    self.ui.add_card(barcode, image, name, weight, price)
                    total_quantity = sum(
                    int(card_frame.findChild(QtWidgets.QLabel, "quantity_label").text().split(" ")[1]) for card_frame in self.ui.card_frames
                    )
                    total_price = sum(
                        float(card_frame.findChild(QtWidgets.QLabel, "price_label").text().split("â‚±")[1]) * int(card_frame.findChild(QtWidgets.QLabel, "quantity_label").text().split(" ")[1])
                        for card_frame in self.ui.card_frames
                    )
                    self.ui.update_total_labels(total_quantity, total_price)
                    # Check if a card with the same barcode already exists
                    # for card_frame in self.ui.card_frames:
                    #     if card_frame.objectName() == f"card_{barcode}":
                    #         print(f"Card with barcode {barcode} already exists.")
                    #         break
                    #     else:
                            
                    #         for index, card_frame in enumerate(self.ui.card_frames, start=1):
                    #             print(f"Card {index} Content:")
                    #             print(f"Product Name: {card_frame.findChild(QtWidgets.QLabel, 'product_label').text()}")
                    #             print(f"Product Weight: {card_frame.findChild(QtWidgets.QLabel, 'product_weight').text()}")
                    #             print(f"Product Price: {card_frame.findChild(QtWidgets.QLabel, 'price_label').text()}")

            if self.camera_on:
                QtCore.QTimer.singleShot(1, self.scan_barcode)
        else:
            self.cap.release()
            cv2.destroyAllWindows()

    def stop_scan(self):
        self.camera_on = False

