    def add_card(self,barcode,product_image,product_name,product_weight,product_price):
        for card_frame in self.card_frames:
            if card_frame.objectName() == f"card_{barcode}":
                quantity_label = card_frame.findChild(QtWidgets.QLabel, "quantity_label")
                
                current_quantity = int(quantity_label.text().split(" ")[1])
