    def remove_card(self, card_frame):
        self.verticalLayout_4.removeWidget(card_frame)
        card_frame.deleteLater()
        self.card_frames.remove(card_frame)
        total_quantity = sum(int(card.findChild(QtWidgets.QLabel, "quantity_label").text().split(" ")[1]) for card in self.card_frames)
        total_price = sum(float(card.findChild(QtWidgets.QLabel, "price_label").text().split("â‚±")[1]) * int(card.findChild(QtWidgets.QLabel, "quantity_label").text().split(" ")[1]) for card in self.card_frames)
        self.update_total_labels(total_quantity, total_price)
